export class Component {
    constructor(id, data) {
        /**
         * @type {string}
         */
        this.id = id;

        /**
         * @type {Object}
         */
        this.data = data;

        /**
         * @type {Entity}
         */
        this.entity = null;

        /**
         * @type {string[]}
         */
        this.dependencies = [];
    }

    /**
     * This is called if attached while game is not running
     */
    offlineAttach(entity) {
        this._checkDependencies(entity);
    }

    /**
     * This is called when it is attached to an Entity
     */
    onAttach() {}

    /**
     * This is called when it is detatched from an Entity
     */
    onDetatch() {}

    setDependency(id) {
        this.dependencies.push(id);
    }

    /**
     * This will check if the entity it's being attached to, has this component's dependencies.
     */
    _checkDependencies(entity) {
        let missingDependencies = [];

        this.dependencies.forEach((dependency) => {
            let isMissing = true;

            for(let i=0; i < Object.keys(entity.components).length; i++) {
                if(Object.keys(entity.components)[i] == dependency) {
                    isMissing = false;
                }
            }

            if(isMissing) missingDependencies.push(dependency);
        });

        if(missingDependencies.length > 0) {
            throw new Error(`${this.id} requires ${missingDependencies.length} other component dependencies. Make sure ${this.id} is added after it's dependencies.`);
        }
    }
}

import { Component } from './component.js';

export class Entity {
    constructor(id, game, components) {
        /**
         * @type {string}
         */
        this.id = id;

        /**
         * @type {Object}
         */
        this.components = {};

        /**
         * @type {string[]}
         */
        this._components = components;

        /**
         * @type {Game}
         */
        this.game = game;

        /**
         * @type {number}
         */
        this._x = 0;

        /**
         * @type {number}
         */
        this._y = 0;

        /**
         * @type {Pixi.Container}
         */
        this.container;
        this.container = new PIXI.Container();
        this.container.gameId = this.id;
    }

    set x(value) {
        if(this.container) {
            this._x = value;
            this.container.x = value;
        }
    }

    get x() {
        return this._x;
    }

    set y(value) {
        if(this.container) {
            this._y = value;
            this.container.y = value;
        }
    }

    get y() {
        return this._y;
    }

    /**
     * Attach a Component to this Entity
     * @param {Component} component
     */
    attach(component) {
        if(this.components[component.id] === undefined) {
            this.components[component.id] = component;

            if(this.game.components[component.id] === undefined) {
                this.game.components[component.id] = [];
            }
            this.game.components[component.id].push(component);
            component.entity = this;

            this.componentCallback(component);

        } else if(!(component instanceof Component))  {
            throw new TypeError(`Not of type Component`);
        } else {
            throw new Error(`There is already a component with the ID '${component.id}'`);
        }
    }

    attachComponents() {
        if(this._components) {
            for(let i=0; i<this._components.length; i++) {
                this.attach(this._components[i]);
            }
        }
    }

    /**
     * Detatch a component from entity
     * @param {string} ID of component
     */
    detach(id) {
        this.components[id].onDetatch(); // First call onDetatch before removing it
        delete this.components[id]; // Remove from Entity map

        // Remove from Game global component list
        this.game.components[id].forEach((component) => {
            if(component.entity.id == this.id) {
                let index = this.game.components[id].indexOf(component);
                if(index > -1) {
                    this.game.components[id].splice(index, 1);
                }
            }
        });
    }

    /**
     * Find a specific Component from this Entity
     * @param {string} id - ID of the component
     * @return {Component}
     */
    find(id) {
        if(this.components[id] !== undefined) {
            return this.components[id];
        }
        throw new Error(`Cannot find Component '${id}' on Entity '${this.id}'`);
    }

    /**
     * Whenever a component is attached, it's callbacks are called
     * @param {Component} component 
     */
    componentCallback(component) {
        if(this.game.running) {
            component.onAttach();
        } else {
            component.offlineAttach(this);
        }
    }

    listenSignal(signalId, callback) {
        this.game.signal.bind(signalId, this, callback);
    }

    sendSignal(signalId, data) {
        this.game.signal.send(signalId, data);
    }

}

import { Entity } from './entity.js';
import { System } from './system.js';
import { Signal } from './signal.js';
import * as PIXI from 'pixi.js';

export class Game {

    /**
     * The Game class takes in dimensions of the game screen
     * @param {number} width - width of game screen
     * @param {number} height - height of game screen
     * @param {Object} extra settings for the renderer
     */
    constructor(width, height, rendererSettings) {
        /**
         * @type {boolean}
         */
        this.running = false;

        /**
         * @type {boolean}
         */
        this.hasSetup = false;

        /**
         * @type {number}
         */
        this.width = width;

        /**
         * @type {number}
         */
        this.height = height;

        /**
         * @type {Object}
         */
        this.entities = {};

        /**
         * @type {Object}
         */
        this.components = {};

        /**
         * @type {Object}
         */
        this.systems = {};

        /**
         * @type {Object}
         */
        this.callbacks = {};

        /**
         * @type {Array}
         */
        this.callbacks.onStart = [];

        /**
         * @type {Array}
         */
        this.callbacks.onUpdate = [];

        /**
         * @type {Object}
         */
        this.rendererSettings = rendererSettings;

        /**
         * @type {Signal}
         */
        this.signal = new Signal();
    }

    /**
     * The game start function. Use onStart() callback to hook in here
     */
    start() {
        this.running = true;
        this._rendererSetup(this.rendererSettings);
        this.onStart();

        // Run start callbacks
        this.callbacks.onStart.forEach((systemObj) => {
            systemObj.callback(systemObj.system);
        });

        // Run component callbacks
        for(let i=0; i < Object.keys(this.entities).length; i++) {
            let entity = this.entities[Object.keys(this.entities)[i]];
            this.addEntityToStage(entity);
            for(let j=0; j < Object.keys(entity.components).length; j++) {
                entity.componentCallback(entity.components[Object.keys(entity.components)[j]]);
            }
        };

        // Start game loop
        global.requestAnimationFrame(this.update.bind(this));
    }

    stop() {
        this.running = false;
    }

    /**
     * The game update function. Use onUpdate() callback to hook into here
     */
    update(time) {
        if(!this.running) return;

        this.onUpdate(time);

        this.callbacks.onUpdate.forEach((systemObj) => {
            systemObj.callback(systemObj.system, time);
        });

        global.requestAnimationFrame(this.update.bind(this));
    }

    /**
     * The game update that happens every game frame/tick.
     */
    onUpdate(time) {
        // Override
    }

    /**
     * The intialization function for the game.
     */
    onStart() {
        // Override
    }

    /**
     * Create an array of entities & add them to the game
     * @param {Entity[]} entities An array of entities
     */
    createEntities(entities) {
        for(let i=0; i<entities.length; i++) {
            if(entities[i] instanceof Entity) {
                this.addEntity(entities[i]);
            } else {
                throw new TypeError(`Trying to add an entity which is not of type 'Entity'`);
            }
        }
    }

    /**
     * Create & add a single entity
     * @param {Entity} entity
     */
    addEntity(entity) {
        if(entity instanceof Entity) {
            this.entities[entity.id] = entity;
            entity.game = this;
            entity.attachComponents();
            this.addEntityToStage(entity);
        } else {
            throw new TypeError(`Trying to add an entity which is not of type 'Entity'`);
        }

    }

    /**
     * Create & add multiple entities
     * @param {Entity[]} array of entities
     */
    addEntities(entities) {
        entities.forEach((entity) => {
            if(entity instanceof Entity) {
                this.entities[entity.id] = entity;
                entity.game = this;
                entity.attachComponents();
                this.addEntityToStage(entity);
            } else {
                throw new TypeError(`Trying to add an entity which is not of type 'Entity'`);
            }
        });
    }



    /**
     * Remove entity from game
     * @param {string} ID of entity
     */
    removeEntity(id) {
        if(this.entities[id] !== undefined) {
            // Remove Pixi child for this entity
            this.stage.children.forEach((child) => {
                if(child.gameId == id) {
                    this.stage.removeChild(child);
                }
            });

            // Remove components in the game global list
            let entity = this.entities[id];
            let compIds = Object.keys(entity.components);

            compIds.forEach((compId) => {
                this.components[compId].forEach((comp) => {
                    if(comp.entity.id == id) {
                        let index = this.components[comp.id].indexOf(comp);
                        if(index > -1) {
                            this.components[comp.id].splice(index, 1);
                        }
                    }
                });
            });

            delete this.entities[id];
        }
    }

    /**
     * Register a system in the game instance
     * @param {System} system
     * @return {System}
     */
    addSystem(system) {
        if(this.systems[system.id] === undefined && system instanceof System) {
            this._registerSystem(system);
            return system;
        } else {
            throw(new TypeError(`Trying to add system which is not type of 'System'`));
        }
    }

    /**
     * Register multiple systems in the game instance
     * @param {System[]} systems
     */
    addSystems(systems) {
        systems.forEach((system) => {
            this.addSystem(system)
        });
    }

    _registerSystem(system) {
        system.game = this;
        this.systems[system.id] = system;

        if(system.onStart) {
            this.callbacks.onStart.push({system: system, callback: system.onStart});
        }

        if(system.onUpdate) {
            this.callbacks.onUpdate.push({system: system, callback: system.onUpdate});
        }
    }

    /**
     * Retrieve an entity
     * @param {string} id - The ID of the entity
     * @return {Entity} Returns the matching entity
     */
    getEntity(id) {
        if(this.entities[id] !== undefined) {
            return this.entities[id];
        }
    }

    /**
     * Retrieves all entities that have all provided components.
     * @param {string[]} components - A list of component ID's to match against
     * @return {Entity[]}
     */
    getEntitiesWith(components) {
        let entities = [];

        for(let i=0; i<Object.keys(this.entities).length; i++) {
            let entity = this.entities[Object.keys(this.entities)[i]];
            let count = 0;
            let target = components.length;

            for(let j=0; j<Object.keys(entity.components).length; j++) {
                let key = Object.keys(entity.components)[j];
                let result = components.find(c => c == entity.components[key].id);
                if(result) count++
            }

            if(count == target) {
                entities.push(entity);
            }
        }
        return entities;
    }

    /**
     * Retrive a system in the game
     * @param {string} id - The ID of the system
     */
    getSystem(id) {
        return this.systems[id];
    }

    /**
     * Retrieves a component from a specific entity
     * @param {string} entityId - The entity to retrieve the component from
     * @param {string} componentId - The component ID
     * @return {Component}
     */
    getComponentFromEntity(entityId, componentId) {
        return this.getEntity(entityId).find(componentId);
    }

    _rendererSetup(settings) {
        if(!this.hasSetup) {
            this.PIXI = PIXI;
            PIXI.utils.skipHello(); // Disables console log stuff;
            this.application = new PIXI.Application({
                width: this.width,
                height: this.height,
                antialias: settings.antialias ? settings.antialias : false,
                transparent: settings.transparent ? settings.transparent : false,
                resolution: settings.resolution ? settings.resolution : 1
            });
            this.stage = this.application.stage;
            this.renderer = this.application.renderer;
            this.renderer.autoResize = true;

            document.body.appendChild(this.application.view);
            this.hasSetup = true;
        };
    }

    addEntityToStage(entity) {
        if(this.stage) {
            this.stage.addChild(entity.container);
        }
    }
}

export class Signal {
    constructor() {
        this.signals = {};
    }

    /**
     * Listen for a specific signal with ID and call the function
     * @param {string} id - An ID of the signal
     * @param {System} system 
     * @param {function} callback 
     */
    bind(id, system, callback) {
        if(!this.signals[id]) {
            this.signals[id] = [];
        }

        this.signals[id].push({ system: system, callback: callback });
    }

    /**
     * Unbind a message & Remove it from the list of signals
     * @param {string} id 
     * @param {System} system 
     */
    unbind(id, system) {
        if(this.signals[id]) {
            this.signals[id].forEach((signal) => {
                if(signal.system.id == system.id) {
                    let index = this.signals[id].indexOf(signal);
                    this.signals[id].splice(index, 1);
                }
            });
        }
    }

    /**
     * Send a signal with data
     * @param {string} id 
     * @param {Object} data 
     */
    send(id, data) {
        if(this.signals[id]) {
            this.signals[id].forEach((signal) => {
                signal.callback(data);
            });
        }
    }
}

export class System {
    constructor(id, onStart, onUpdate) {
        /**
         * @type {string}
         */
        this.id = id;

        /**
         * @type {Game}
         */
        this.game = null;

        /**
         * @type {function}
         */

        this.onStart = onStart;
        this.onUpdate = onUpdate;
    }

    /**
     * Retrieves an Entity from the Game
     * @param {string} id - ID of the entity
     * @return {Entity}
     */
    getEntity(id) {
        if(this.game !== null) {
            return this.game.getEntity(id);
        }
    }

    /**
     * Retrieves a Components from a specific Entity
     * @param {string} entityId - The ID of the Entity
     * @param {string} componentId - The ID of the Component
     */
    getComponentFromEntity(entityId, componentId) {
        if(this.game) {
            let entity = this.game.getEntity(entityId);
            let component = entity.find(componentId)
            return component;
        }
    }

    listenSignal(signalId, callback) {
        this.game.signal.bind(signalId, this, callback);
    }

    sendSignal(signalId, data) {
        this.game.signal.send(signalId, data);
    }
}

import { System } from '../../core/system.js';

export class RenderingSystem extends System {
    constructor(id) {
        super(id, ()=>{}, ()=>{});
        this.onStart = this.start;
        this.onUpdate = this.update;
    }

    start(system) {
        system.listenSignal('shape.render', (data) => {
            system.renderShape(system, data.entity);
        });
    }

    update(system, time) {}


    /**
     * Renders a shape on an entity with a Shape component
     * @param {System} system 
     * @param {Entity} entity 
     */
    renderShape(system, entity) {
        let comp = entity.find('ShapeComponent');

        if(comp) {
            switch(comp.data.type) {
                case 'circle':
                    let circle = system._drawCircle(system, entity.x, entity.y, comp.data.radius, comp.data.color);
                    entity.container.addChild(circle);
                    break;
                case 'rect':
                    let rect = system._drawRect(entity.x, entity.y, comp.data.width, comp.data.height, comp.data.color);
                    entity.container.addChild(rect);
                    break;
            }
        }
    }

    /**
     * Creates a Circle Pixi Graphics object
     * @param {System} system 
     * @param {number} x 
     * @param {number} y 
     * @param {number} radius 
     * @param {color} color - eg. '0xFFFFFF' or 'white'
     * @returns {Pixi.Graphic} Pixi Graphics object
     */
    _drawCircle(system, x, y, radius, color) {
        let gfx = new this.game.PIXI.Graphics();
        gfx.beginFill(color);
        gfx.drawCircle(x, y, radius);
        gfx.endFill();
        return gfx;
    }

    /**
     * Creates a Rect Pixi Graphics object
     * @param {number} x 
     * @param {number} y 
     * @param {number} width 
     * @param {number} height 
     * @param {color} color - eg. '0xFFFFFF' or 'white'
     * @returns {Pixi.Graphic} Pixi Graphics object
     */
    _drawRect(x, y, width, height, color) {
        let gfx = new this.game.PIXI.Graphics();
        gfx.beginFill(color);
        gfx.drawRect(x, y, width, height); 
        gfx.endFill();
        return gfx;
    }
}

